}

return icon(value) + '<span style="vertical-align: super;padding-left: 8px;">' + capitalize(value) + '</span>';

actionCellButton.showWidgetActionFunction:
if (!data.addr) {
    if (widgetContext.dashboard.authUser.authority == 'TENANT_ADMIN') {
        return true;
    }
    if (check_perms('DEVICE', 'WRITE')) {
        return true;
    }
}
return false;

function check_perms(entityType, operation) {
    let $injector = widgetContext.$scope.$injector; 
    let userPermissionsService = $injector.get(widgetContext.servicesMap.get('userPermissionsService'));
    let perms = userPermissionsService.userPermissions;
    let entityTypes = [entityType, 'ALL'];
    let operations = [operation, 'ALL'];
    for (let i in entityTypes) {
        if (perms.genericPermissions.hasOwnProperty(entityTypes[i])) {
            for (let j in operations) {
                if (perms.genericPermissions[entityTypes[i]].indexOf(operations[j]) > -1) {
                    return true;
                }
            }
        }
    }
    for (let key in perms.groupPermissions) {
        if (perms.groupPermissions[key].entityType === entityType && perms.groupPermissions[key].operations.some(op => operations.includes(op)) ) {
            return true;
        }
    }
    return false;
}

actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entity = {};

    vm.editEntityFormGroup = vm.fb.group({
        firmwareId: [null]
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        saveEntity().subscribe(
            function () {
                // widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };


    function getEntityInfo() {
        entityService.getEntity(entityId.entityType, entityId.id).subscribe(
            function (data) {
                vm.entity = data;
                vm.editEntityFormGroup.patchValue({
                    firmwareId: vm.entity.firmwareId
                }, {emitEvent: false});
            }
        );
    }

    function saveEntity() {
        const formValues = vm.editEntityFormGroup.value;
        vm.entity.firmwareId = formValues.firmwareId;
        return deviceService.saveDevice(vm.entity);
    }
}

