rowClick.customFunction:
let $scope = widgetContext.$scope;
let $injector = $scope.$injector;
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
return goToMoveOrganigramState();

async function goToMoveOrganigramState() {
    var query = {
        parameters: {
            rootId: entityId.id,
            rootType: entityId.entityType,
            direction: "TO",
            maxLevel: 1
        },
        filters: [{
            relationType: 'Organigram',
            entityTypes: ["ASSET"]
        }]
    };
    let resp = await entityRelationService.findInfoByQuery(query).toPromise();
    let assetId = resp[0].from.id;
    $('.jstree-search').removeClass('jstree-search');
    $('#'+assetId).parent().parent().addClass('jstree-search');
}

