headerButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get("customDialog"));

openAddAssetDialog();

function openAddAssetDialog() {
	customDialog.customDialog(htmlTemplate, AddAssetDialogController).subscribe();
}

async function AddAssetDialogController(instance) {
	let vm = instance;
	vm.iconStates = getIconStates();
	vm.helpInfo = getHelpInfo();

	vm.cancel = function () {
		vm.dialogRef.close(null);
	};
}

//Get information of the confirmation states
function getIconStates() {
    let translate = widgetContext.translate;
	return [
	    {icon: 'sd_storage', title:"Flashed", info: translate.instant('firmware-dashboard.iconHelp.flashed')},
	    {icon: 'hourglass_empty', title:"Queued", info: translate.instant('firmware-dashboard.iconHelp.queued')},
	    {icon: 'downloading', title:"Initiated", info: translate.instant('firmware-dashboard.iconHelp.initiated')},
	    //{svgIcon: 'mdi:gesture-swipe-down', title:"Waiting download conditions", info: translate.instant('firmware-dashboard.iconHelp.waiting_download_conditions')},
	    {icon: 'downloading', title:"Downloading", info: translate.instant('firmware-dashboard.iconHelp.downloading')},
	    {icon: 'downloading', title:"Downloaded", info: translate.instant('firmware-dashboard.iconHelp.downloaded')},
	    {icon: 'update', title:"Verified", info: translate.instant('firmware-dashboard.iconHelp.verified')},
	    {svgIcon: 'mdi:gesture-tap-button', title:"Waiting installation authorization", info: translate.instant('firmware-dashboard.iconHelp.waiting_installation_authorization')},
	    {svgIcon: 'mdi:cellphone-cog', title:"Waiting installation conditions", info: translate.instant('firmware-dashboard.iconHelp.waiting_installation_conditions')},
	    {icon: 'update', title:"Updating", info: translate.instant('firmware-dashboard.iconHelp.updating')},
	    {icon: 'install_desktop', title:"Installed", info: translate.instant('firmware-dashboard.iconHelp.installed')},
	    {icon: 'download_done', title:"Updated", info: translate.instant('firmware-dashboard.iconHelp.updated')},
	    {icon: 'warning', title:"Failed", info: translate.instant('firmware-dashboard.iconHelp.failed')},
	    {svgIcon: 'mdi:monitor-shimmer', title:"Unassigned", info: translate.instant('firmware-dashboard.iconHelp.clean')},
    ];
}

//Get extra help information
function getHelpInfo() {
    return [];
}

