settings.nodeRelationQueryFunction:
// Function should return relations query object for current node used to fetch entity children.
// Function can return 'default' string value. In this case default relations query will be used.

// The following example code will construct simple relations query that will fetch relations of type 'Contains'
// from the current entity.

var entity = nodeCtx.entity;
var query = {
    parameters: {
        rootId: entity.id.id,
        rootType: entity.id.entityType,
        direction: "FROM",
        maxLevel: 1
    },
    filters: [{
        relationType: "Organigram",
        entityTypes: ["ASSET"]
        //entityTypes: ["ASSET", "DEVICE"]
    }]
};
return query;

settings.nodeHasChildrenFunction:
/**

// Function should return boolean value indicating whether current node has children (whether it can be expanded).

// The following example code will restrict entities hierarchy expansion up to third level.

return nodeCtx.level <= 2;

// The next example code will restrict entities expansion according to the value of example 'nodeHasChildren' attribute.

var data = nodeCtx.data;
if (data.hasOwnProperty('nodeHasChildren') && data['nodeHasChildren'] !== null) {
    return data['nodeHasChildren'] === 'true';
} else {
    return true;
}

**/

var entity = nodeCtx.entity;
if (entity.id.entityType == "ASSET") {
    return true;
} else {
    return false;
}

settings.nodeOpenedFunction:
// Function should return boolean value indicating whether current node should be opened (expanded) when it first loaded.

// The following example code will open by default nodes up to third level.

setTimeout(function() {
    if (typeof widgetCtx.$container !== undefined) {
        if ($(widgetCtx.$container[0]).find('.jstree-clicked').length == 0 ) {
            let selectedFolder = widgetCtx.stateController.getStateParams().OnOrganigramSelected;
            let assetId = selectedFolder ? selectedFolder.entityId.id : widgetCtx.datasources[0].entityId;
            $(widgetCtx.$container[0]).find('#'+assetId).click();
            //$(widgetCtx.$container[0]).find('#'+assetId).parent().addClass('jstree-clicked');
        }
    }
}, 500);
return nodeCtx.level <= 100;

settings.nodeDisabledFunction:
// Function should return boolean value indicating whether current node should be disabled (not selectable).

var entity = nodeCtx.entity;
let $injector = widgetCtx.$scope.$injector; 
let userPermissionsService = $injector.get(widgetCtx.servicesMap.get('userPermissionsService'));
let perms = userPermissionsService.userPermissions;
if (check_perms('DEVICE', 'READ') || entity.name.includes('--Root')) {
    return false;
}

if (!widgetCtx.$scope.deviceGroups) {
    get_device_groups();
}
return entity_disabled_function();

function entity_disabled_function() {
    return !check_device_groups();
}

function check_perms(entityType, operation) {
    let entityTypes = [entityType, 'ALL'];
    let operations = [operation, 'ALL'];
    for (let i in entityTypes) {
        if (perms.genericPermissions.hasOwnProperty(entityTypes[i])) {
            for (let j in operations) {
                if (perms.genericPermissions[entityTypes[i]].indexOf(operations[j]) > -1) {
                    return true;
                }
            }
        }
    }
    return false;
}

function check_device_groups() {
    if (perms.readGroupPermissions.DEVICE.entityGroupIds.length > 0 && widgetCtx.$scope.deviceGroups) {
        return widgetCtx.$scope.deviceGroups.some(g => g.name === entity.name);
    }
    return false;
}

async function get_device_groups() {
    let entityGroupService = $injector.get(widgetCtx.servicesMap.get('entityGroupService'));
    let groupIds = perms.readGroupPermissions.DEVICE.entityGroupIds.map(g => g.id);
    let groups = await entityGroupService.getEntityGroupEntityInfosByIds(groupIds).toPromise();
    widgetCtx.$scope.deviceGroups = groups;
    return entity_disabled_function();
}

settings.nodeIconFunction:
// Function should return node icon info object.
// Resulting object should contain either 'materialIcon' or 'iconUrl' property. 
// Where:
//    - 'materialIcon' - name of the material icon to be used from the Material Icons Library (https://material.io/tools/icons);
//    - 'iconUrl' - url of the external image to be used as node icon.
// Function can return 'default' string value. In this case default icons according to entity type will be used.

// The following example code shows how to use external image for devices which name starts with 'Test' and use 
// default icons for the rest of entities.

function getRootNamespace(nodeCtx) {
    if (nodeCtx.level == 1) {
        let name = nodeCtx.entity.name;
        return splitEntityName(name)[0];
    }
    return getRootNamespace(nodeCtx.parentNodeCtx);
}

function splitEntityName(name) {
    return name.split("--");
}

var entity = nodeCtx.entity;
let icon = 'default';
if (entity.id.entityType == "ASSET") {
var rootNamespace = getRootNamespace(nodeCtx);
var nameSplit = splitEntityName(entity.name);
    if (nameSplit[0] === rootNamespace) {
        icon = nameSplit[1] === 'Root' ? 'radio_button_checked' : (nameSplit[1] === 'Pending' ? 'folder_special' : 'folder');

    } else {
        icon = nameSplit[1] === 'Root' ? 'radio_button_unchecked' : (nameSplit[1] === 'Pending' ? 'crop_din' : 'folder_open');
    }
    return {materialIcon: icon};
}else if(entity.id.entityType == "CUSTOMER" || entity.id.entityType == "TENANT") {
    return null;
} else {
    return 'default';
}

settings.nodeTextFunction:
// Function should return text (can be HTML code) for the current node.

var entity = nodeCtx.entity;
let $injector = widgetCtx.$scope.$injector; 
let userPermissionsService = $injector.get(widgetCtx.servicesMap.get('userPermissionsService'));
let perms = userPermissionsService.userPermissions;
var entity = nodeCtx.entity;
if (entity.id.entityType != "ASSET") {
    return '';
}
if (check_perms('DEVICE', 'READ') || entity.name.includes('--Root')) {
    return entity_text();
}
if (!widgetCtx.$scope.deviceGroups) {
    return get_device_groups();
}
return entity_text_function();

function entity_text() {
    var text = entity.label ? entity.label : entity.name;
    return "<span id='"+ entity.id.id + "'>" + text + "</span>";
}

function entity_text_function() {
    if (check_device_groups()) {
        return entity_text();
    }
    return '';
}

function check_perms(entityType, operation) {
    let entityTypes = [entityType, 'ALL'];
    let operations = [operation, 'ALL'];
    for (let i in entityTypes) {
        if (perms.genericPermissions.hasOwnProperty(entityTypes[i])) {
            for (let j in operations) {
                if (perms.genericPermissions[entityTypes[i]].indexOf(operations[j]) > -1) {
                    return true;
                }
            }
        }
    }
    return false;
}

function check_device_groups() {
    if (perms.readGroupPermissions.DEVICE.entityGroupIds.length > 0 && widgetCtx.$scope.deviceGroups) {
        return widgetCtx.$scope.deviceGroups.some(g => g.name === entity.name);
    }
    return false;
}

async function get_device_groups() {
    let entityGroupService = $injector.get(widgetCtx.servicesMap.get('entityGroupService'));
    let groupIds = perms.readGroupPermissions.DEVICE.entityGroupIds.map(g => g.id);
    let groups = await entityGroupService.getEntityGroupEntityInfosByIds(groupIds).toPromise();
    widgetCtx.$scope.deviceGroups = groups;
    return entity_text_function();
}

settings.nodesSortFunction:
/**

// This function is used to sort nodes of the same level. Function should compare two nodes and return 
// integer value: 
//     - less than 0 - sort nodeCtx1 to an index lower than nodeCtx2
//     - 0 - leave nodeCtx1 and nodeCtx2 unchanged with respect to each other
//     - greater than 0 - sort nodeCtx2 to an index lower than nodeCtx1

// The following example code will sort entities first by entity type in alphabetical order then
// by entity name in alphabetical order.

var result = nodeCtx1.entity.id.entityType.localeCompare(nodeCtx2.entity.id.entityType);
if (result === 0) {
    result = nodeCtx1.entity.name.localeCompare(nodeCtx2.entity.name);
}
return result;

**/

