/*=======================================================================*/
/*========================  Edit entity example  ========================*/
/*=======================================================================*/
let $scope = widgetContext.$scope;
let $injector = $scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let selectedEntity = widgetContext.stateController.getStateParams().OnOrganigramSelected;
let translate = widgetContext.translate;
if (!selectedEntity) {
    let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
    dialogs.alert(translate.instant('event.error'), translate.instant("basic-dashboard.hierarchy-widget.noFolder"), translate.instant('action.close'));
    return false;
}
openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityId = selectedEntity.hasOwnProperty('entityId') ? selectedEntity.entityId : entityId;

    vm.editEntityFormGroup = vm.fb.group({
        entityLabel: [null, [vm.validators.required]],
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        widgetContext.rxjs.forkJoin([
            saveEntity()
        ]).subscribe(
            function () {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };

    async function getEntityInfo() {
        widgetContext.rxjs.forkJoin([
            assetService.getAsset(vm.entityId.id)
        ]).subscribe(
            function success(data) {
                vm.entity = data[0];
                getDeviceGroups();
                vm.entityLabel = vm.entity.label;
                vm.editEntityFormGroup.patchValue({
                    entityLabel: vm.entity.label
                }, {emitEvent: false});
            },
            function fail(){
                vm.dialogRef.close(null);
            }
        );
    }

    function saveEntity() {
        const formValues = vm.editEntityFormGroup.value;
        let tasks = [];
        let nameSplit = vm.entity.name.split("--");
        let group = getDeviceGroupByName(vm.groups, vm.entity.name);
        if (nameSplit[1] != 'Pending' && nameSplit[1] != 'Root') {
            vm.entity.name = nameSplit[0] + '--' + formValues.entityLabel;
        }
        vm.entity.label = formValues.entityLabel;
        tasks.push(assetService.saveAsset(vm.entity));
        // We don't want to create the root group that never will have devices
        if (nameSplit[1] != 'Root') {
            if (!group) {
                group = {
                    type: "DEVICE",
                    ownerId: vm.entity.ownerId,
                    customerId: vm.entity.customerId
                };
            }
            if (!group.additionalInfo || (group.additionalInfo && typeof group.additionalInfo === 'string') ) {
                group.additionalInfo = {};
            } 
            group.additionalInfo.description = formValues.entityLabel;
            group.name = vm.entity.name;
            tasks.push(entityGroupService.saveDeviceEntityGroup(group));
        }
        return widgetContext.rxjs.forkJoin(tasks);
    }

    async function getDeviceGroups() {
        vm.groups = await entityGroupService.getEntityGroupsByOwnerId(vm.entity.ownerId.entityType, vm.entity.ownerId.id, 'DEVICE').toPromise();
    }

    function getDeviceGroupByName(groups, name) {
        for (let i = 0; i < groups.length; i++) {
            let group = groups[i];
            if (group.name == name) {
                return group;
            }
        }
        return null;
    }
}

