/*========================================================================*/
/*=========================  Add entity example  =========================*/
/*========================================================================*/
let $scope = widgetContext.$scope;
let $injector = $scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let selectedEntity = widgetContext.stateController.getStateParams().OnOrganigramSelected;
let translate = widgetContext.translate;
if (!selectedEntity) {
    dialogs.alert(translate.instant('event.error'), translate.instant("basic-dashboard.hierarchy-widget.noFolder"), translate.instant('action.close'));
    return false;
}

openAddEntityDialog();

function openAddEntityDialog() {
    customDialog.customDialog(htmlTemplate, AddEntityDialogController).subscribe();
}

function AddEntityDialogController(instance) {
    let vm = instance;

    let nameSplit = selectedEntity.entityName.split("--");
    if (nameSplit[1] == 'Pending') {
        dialogs.alert(
            translate.instant('event.error'), 
            translate.instant('basic-dashboard.hierarchy-widget.createNotAllowed', {
                "entityLabel": selectedEntity.entityLabel
            }), 
            translate.instant('action.close')
        );
        vm.dialogRef.close(null);
    }

    vm.selectedEntity = selectedEntity;

    vm.addEntityFormGroup = vm.fb.group({
     entityLabel: ['', [vm.validators.required]],
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.addEntityFormGroup.markAsPristine();
        saveEntityObservable().subscribe(
            function (entity) {
                widgetContext.rxjs.forkJoin([
                    saveRelations(entity.id),
                    saveGroupObservable(entity)
                ]).subscribe(
                    function () {
                        widgetContext.updateAliases();
                        vm.dialogRef.close(null);
                    }
                );
            }
        );
    };

    function saveEntityObservable() {
        const formValues = vm.addEntityFormGroup.value;
        let entityName = vm.namespacePrefix + '--' + formValues.entityLabel;
        let entity = {
            name: entityName,
            type: selectedEntity.relationType,
            label: formValues.entityLabel,
            ownerId: selectedEntity.ownerId,
            customerId: selectedEntity.customerId
        };
        return assetService.saveAsset(entity);
    }

    function saveGroupObservable(asset) {
        let entity = {
            name: asset.name,
            additionalInfo: {description: asset.label},
            type: "DEVICE",
            ownerId: selectedEntity.ownerId,
            customerId: selectedEntity.customerId
        };
        return entityGroupService.saveDeviceEntityGroup(entity);
    }

    function getEntityInfo() {
        widgetContext.rxjs.forkJoin([
            assetService.getAsset(selectedEntity.entityId.id),
        ]).subscribe(
            function success (data) {
                selectedEntity.ownerId = data[0].ownerId;
                selectedEntity.customerId = data[0].customerId;
                getNamespacePrefix(data[0].ownerId);
            },
            function fail () {
                vm.dialogRef.close(null);    
            }
        );
    }

    function getNamespacePrefix(namespaceEntityId) {
        widgetContext.rxjs.forkJoin([
            attributeService.getEntityAttributes(namespaceEntityId, 'SERVER_SCOPE'),
        ]).subscribe(
            function success (data) {
                let entityattributes = getEntityAttributes(data[0]);
                if (!entityattributes.hasOwnProperty("namespacePrefix")) {
                    dialogs.alert(
                        translate.instant('event.error'), 
                        translate.instant('basic-dashboard.hierarchy-widget.noNamespace'), 
                        translate.instant('action.close')
                    );
                    vm.dialogRef.close(null);
                }
                vm.namespacePrefix = entityattributes.namespacePrefix;
            },
            function fail() {
                vm.dialogRef.close(null);
            }
        );
    }

    function getEntityAttributes(attributes) {
        let attrObj = {};
        for (var i = 0; i < attributes.length; i++) {
            attrObj[attributes[i].key] = attributes[i].value;
        }
        return attrObj;
    }

    function saveRelations(entityId) {
        let data = dataArrayToObject(widgetContext.data);
        let relation = {
            type: selectedEntity.relationType,
            from: selectedEntity.entityId,
            to: entityId
        };
        return entityRelationService.saveRelation(relation);
    }

    function dataArrayToObject(arr) {
    	return arr.reduce((obj, item) => {
    		return {
    			...obj,
    			[item["dataKey"]["name"]]: item.data[0] && item.data[0][1],
    		};
    	}, {});
    }

    function camelize(str) {
       return str.replace(/\W+(.)/g, function(match, chr)
       {
            return chr.toUpperCase();
        });
    }
}

