let $scope = widgetContext.$scope;
let $injector = $scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let selectedEntity = widgetContext.stateController.getStateParams().OnOrganigramSelected;
let translate = widgetContext.translate;
if (!selectedEntity) {
    let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
    dialogs.alert(translate.instant('event.error'), translate.instant("basic-dashboard.hierarchy-widget.noFolder"), translate.instant('action.close'));
    return false;
}

let pendingFolder;
openDeleteEntityDialog();

async function openDeleteEntityDialog() {
    let title = translate.instant('action.delete') + ' ' + selectedEntity.entityLabel;
    if (endsWith(selectedEntity.entityName, 'Root') || endsWith(selectedEntity.entityName, 'Pending')) {
        let content = translate.instant('basic-dashboard.hierarchy-widget.deleteRequiredFolderError', {
            "folderName": selectedEntity.entityLabel,
        });         
        dialogs.alert(title, content, translate.instant('action.cancel'));
    } else {
        pendingFolder = await getPendingFolder(selectedEntity.entityName);
        if (!pendingFolder) {
            showErrorDialog();
            return false;
        }
        let content = translate.instant('basic-dashboard.hierarchy-widget.deleteFolderConfirmation', {
            "folderName": selectedEntity.entityLabel,
            "pendingFolder": pendingFolder.label
        });

        dialogs.confirm(title, content, translate.instant('action.cancel'), translate.instant('action.delete')).subscribe(
            function(result) {
                if (result) {
                    deleteEntity();
                }
            }
        );
    }
}

function endsWith(str, suffix) {
    return str.indexOf(suffix, str.length - suffix.length) !== -1;
}

async function deleteEntity() {
    let relations = await getEntityFromRelations();
    let groups = await getDeviceGroups();
    if (relations.to.length == 0) {
        showErrorDialog();
        return false;
    }
    widgetContext.rxjs.forkJoin([
        saveNewRelations(relations, groups),
        deleteEntityObservable(),
        deleteGroupObservable(groups)
    ]).subscribe(
        function success() {
            widgetContext.updateAliases();
        },
        function fail() {
            showErrorDialog();
        }
    );
}

function deleteEntityObservable() {
    return assetService.deleteAsset(selectedEntity.entityId.id);
}

function deleteGroupObservable(groups) {
    let group = getDeviceGroupByName(groups, selectedEntity.entityName);
    if (group) {
        return widgetContext.rxjs.forkJoin([entityGroupService.deleteEntityGroup(group.id.id)]);
    }
    return widgetContext.rxjs.of([]);

}

async function getEntityFromRelations() {
    let relations = {};
    relations.to = await entityRelationService.findByToAndType(selectedEntity.entityId, selectedEntity.relationType).toPromise();
    relations.from = await entityRelationService.findByFromAndType(selectedEntity.entityId, selectedEntity.relationType).toPromise();
    return relations;
}

async function getDeviceGroups() {
    let asset = await assetService.getAsset(selectedEntity.entityId.id).toPromise();
    let groups = await entityGroupService.getEntityGroupsByOwnerId(asset.ownerId.entityType, asset.ownerId.id, 'DEVICE', asset.label).toPromise();
    return groups;
}

function saveNewRelations(relations, groups) {
    let assetRelatedEntity = relations.to[0].from;
    let group = getDeviceGroupByName(groups, pendingFolder.name);
    let tasks = [];
    let deviceIds = [];
    for (var i = 0; i < relations.from.length; i++) {
        let relation = {
            type: selectedEntity.relationType,
            to: relations.from[i].to,
            from: relations.from[i].to.entityType === "ASSET" ? assetRelatedEntity : pendingFolder.id,
        };
        tasks.push(entityRelationService.saveRelation(relation));
        if (relations.from[i].to.entityType == "DEVICE") {
            deviceIds.push(relations.from[i].to.id);
        }
        //aadir a grupo pendingFolder.label
    }
    if (tasks.length > 0) {
        if (deviceIds.length > 0) {
            tasks.push(entityGroupService.addEntitiesToEntityGroup(group.id.id, deviceIds));
        }
        return widgetContext.rxjs.forkJoin(tasks);
    }
    return widgetContext.rxjs.of([]);
}

function getDeviceGroupByName(groups, name) {
    for (let i = 0; i < groups.length; i++) {
        let group = groups[i];
        if (group.name == name) {
            return group;
        }
    }
    return null;
}

async function getPendingFolder(entityName) {
    let pendingFolderName = entityName.split("--")[0] + '--Pending';
    let assets = await assetService.getUserAssets(widgetContext.pageLink(1, 0, pendingFolderName)).toPromise();
    return assets.data.length > 0 ? assets.data[0] : null;
}

function showErrorDialog() {
    let title = translate.instant('event.error');
    let content = translate.instant('basic-dashboard.hierarchy-widget.genericError');
    dialogs.alert(title, content, 'CLOSE').subscribe(
        function(result) {}
    );
}

