let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let entityGroupService = $injector.get(widgetContext.servicesMap.get('entityGroupService'));
let selectedEntity = widgetContext.stateController.getStateParams().OnOrganigramSelected;
if (!selectedEntity) {
    let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
    dialogs.alert(translate.instant('event.error'), translate.instant("basic-dashboard.hierarchy-widget.noFolder"), translate.instant('action.close'));
    return false;
}

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;
    vm.entityId = selectedEntity.hasOwnProperty('entityId') ? selectedEntity.entityId : entityId;
    vm.entityName = selectedEntity.hasOwnProperty('entityLabel') ? selectedEntity.entityLabel : selectedEntity.entityName;

    vm.entity = {};

    vm.editEntityFormGroup = vm.fb.group({
        firmwareId: [null]
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        saveEntity().subscribe(
            function () {
                // widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };


    function getEntityInfo() {
        entityService.getEntity(selectedEntity.entityId.entityType, selectedEntity.entityId.id).subscribe(
            function (asset) {
                entityGroupService.getEntityGroupsByOwnerId(asset.ownerId.entityType, asset.ownerId.id, 'DEVICE', asset.label).subscribe(
                    function (groups) {
                        let group = getDeviceGroupByName(groups, selectedEntity.entityName);
                        if (group) {
                            vm.entity.groupId = group.id;
                            vm.entity.otaPackageType = "FIRMWARE";
                            entityGroupService.otaPackageService.getOtaPackageInfoByDeviceGroupId(group.id.id, 'FIRMWARE').subscribe(
                                    function (otaPackage) {
                                        if (otaPackage) {
                                            vm.entity = otaPackage;
                                            vm.editEntityFormGroup.patchValue({
                                                firmwareId: otaPackage.otaPackageId
                                            }, {emitEvent: false});
                                        }
                                    }
                                );
                        }
                    }
                );
            }
        );
    }

    function saveEntity() {
        const formValues = vm.editEntityFormGroup.value;
        if (formValues.firmwareId) {
            vm.entity.otaPackageId = formValues.firmwareId;
            return entityGroupService.saveDeviceGroupOtaPackage(vm.entity);
        } else if (vm.entity.id) {
            return entityGroupService.deleteDeviceGroupOtaPackage(vm.entity.id);
        }
    }

    function getDeviceGroupByName(groups, name) {
        if (groups) {
            for (let i = 0; i < groups.length; i++) {
                let group = groups[i];
                if (group.name == name) {
                    return group;
                }
            }
        }
        return null;
    }
}

