    }
    if (value == 'INITIATED' || value == 'DOWNLOADING' || value == 'DOWNLOADED') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font">downloading</mat-icon>';
    }
    if (value == 'VERIFIED' || value == 'UPDATING' ) {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font" style="color: #000">update</mat-icon>';
    }
    if (value == 'UPDATED') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font" style="color: #000">download_done</mat-icon>';
    }
    if (value == 'READY_TO_INSTALL') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font" style="color: #000">thumb_up</mat-icon>';
    }
    if (value == 'FAILED') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font" style="color: #D93025">warning</mat-icon>';
    }
    if (value == 'INSTALLED') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font">install_desktop</mat-icon>';
    }
    if (value == 'Flashed by uSD') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font">sd_storage</mat-icon>';
    }
    if (value == 'WAITING_INSTALLATION_AUTHORIZATION') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font" style="color: #043d5d">gesture_tap_button</mat-icon>';
    }
    if (value == 'WAITING_DOWNLOAD_CONDITIONS') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font" style="color: #043d5d">gesture_swipe_down</mat-icon>';
    }
    if (value == 'WAITING_INSTALLATION_CONDITIONS') {
        return '<mat-icon _role="img" class="mat-icon notranslate material-icons mat-icon-no-color" aria-hidden="true" data-mat-icon-type="font" style="color: #043d5d">cellphone_cog</mat-icon>';
    }
    return '';
}
function capitalize (s) {
  if (typeof s !== 'string') return '';
  return s.charAt(0).toUpperCase() + s.slice(1).toLowerCase().replace(/_/g, " ");
}

return icon(value) + '<span style="vertical-align: super;padding-left: 8px;">' + capitalize(value) + '</span>';

actionCellButton.showWidgetActionFunction:
if (!data.addr) {
    if (widgetContext.dashboard.authUser.authority == 'TENANT_ADMIN') {
        return true;
    }
    if (check_perms('DEVICE', 'WRITE')) {
        return true;
    }
}
return false;

function check_perms(entityType, operation) {
    let $injector = widgetContext.$scope.$injector; 
    let userPermissionsService = $injector.get(widgetContext.servicesMap.get('userPermissionsService'));
    let perms = userPermissionsService.userPermissions;
    let entityTypes = [entityType, 'ALL'];
    let operations = [operation, 'ALL'];
    for (let i in entityTypes) {
        if (perms.genericPermissions.hasOwnProperty(entityTypes[i])) {
            for (let j in operations) {
                if (perms.genericPermissions[entityTypes[i]].indexOf(operations[j]) > -1) {
                    return true;
                }
            }
        }
    }
    for (let key in perms.groupPermissions) {
        if (perms.groupPermissions[key].entityType === entityType && perms.groupPermissions[key].operations.some(op => operations.includes(op)) ) {
            return true;
        }
    }
    return false;
}

actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.entity = {};

    vm.editEntityFormGroup = vm.fb.group({
        firmwareId: [null]
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        vm.editEntityFormGroup.markAsPristine();
        saveEntity().subscribe(
            function () {
                // widgetContext.updateAliases();
                vm.dialogRef.close(null);
            }
        );
    };


    function getEntityInfo() {
        entityService.getEntity(entityId.entityType, entityId.id).subscribe(
            function (data) {
                vm.entity = data;
                vm.editEntityFormGroup.patchValue({
                    firmwareId: vm.entity.firmwareId
                }, {emitEvent: false});
            }
        );
    }

    function saveEntity() {
        const formValues = vm.editEntityFormGroup.value;
        vm.entity.firmwareId = formValues.firmwareId;
        return deviceService.saveDevice(vm.entity);
    }
}

